package model;

public class BrandModel {
	private int idB;
	private String nameB;
	
	public BrandModel() {
		super();
		// TODO Auto-generated constructor stub
	}
	public BrandModel(int idB, String nameB) {
		super();
		this.idB = idB;
		this.nameB = nameB;
	}
	public int getIdB() {
		return idB;
	}
	public void setIdB(int idB) {
		this.idB = idB;
	}
	public String getNameB() {
		return nameB;
	}
	public void setNameB(String nameB) {
		this.nameB = nameB;
	}
	

}
